  if  choose_service == "a":
    res = 23
  elif choose_service == "b":
    res = 40
  else : 
    res = 67

  include_surtax = input("부가세를 포함합니까? (y/n)")
  def total_price():
    if include_surtax == "y":
      resp = res *1.1
    elif include_surtax == "n":
      resp = res  
    else:
      print( "다시 입력해주세요")
    print(f"물건 가격은 {res}, 소비자 가격은 {resp} 입니다.")
  total_price()

